(90, 1, '2023-06-23 11:51:00', '2023-06-23 16:51:00', '{\n    \"hestia::hestia_blog_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-06-23 16:33:43\"\n    },\n    \"hestia::hestia_clients_bar_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-06-23 16:32:32\"\n    },\n    \"hestia::hestia_team_hide\": {\n        \"value\": false,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-06-23 16:33:43\"\n    },\n    \"hestia::hestia_team_title\": {\n        \"value\": \"\\\"S\\u00f3lo es posible avanzar cuando se mira lejos.  S\\u00f3lo se puede progresar pensando en grande\\\".\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-06-23 16:35:58\"\n    },\n    \"hestia::hestia_team_subtitle\": {\n        \"value\": \"\\\"It is only possible to advance when one looks far ahead.  One can only make progress by thinking big.\\\"\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2023-06-23 16:36:30\"\n    },\n    \"hestia::hestia_team_content\": {\n        \"value\": \"[{\\\"icon_value\\\":\\\"fab fa-facebook-square\\\",\\\"text\\\":\\\"Publicamos constantemente nuestras \\u00faltimas noticias, logros y nuevos contenidos para mantenerle al d\\u00eda.\\\",\\\"link\\\":\\\"\\\",\\\"text2\\\":\\\"undefined\\\",\\\"image_url\\\":\\\"http://localhost/wordpress/wp-content/uploads/2023/06/image-9-225x300.jpg\\\",\\\"title\\\":\\\"Sigue &amp; Comparte\\\",\\\"subtitle\\\":\\\"Follow &amp; Share\\\",\\\"social_repeater\\\":\\\"[{&quot;icon&quot;:&quot;fab fa-facebook-square&quot;,&quot;link&quot;:&quot;https:&#x2F;&#x2F;www.facebook.com&#x2F;fundacion.avanzar.ecuador&quot;,&quot;id&quot;:&quot;customizer-repeater-social-repeater-57fb908674e06&quot;},{&quot;icon&quot;:&quot;fas fa-cam