ancy\":0.5,\"overlay_state\":\"hover\",\"pagelayer-id\":\"olg6751\"} -->\n<!-- wp:pagelayer/pl_col {\"widget_space\":15,\"overlay_hover_delay\":400,\"pagelayer-id\":\"aql8412\"} -->\n<!-- wp:pagelayer/pl_wp_wpforms-widget {\"widget_data\":\"{\\u0022title\\u0022:\\u0022Contactos\\u0022,\\u0022form_id\\u0022:\\u002282\\u0022,\\u0022show_title\\u0022:\\u00221\\u0022,\\u0022show_desc\\u0022:\\u00221\\u0022}\",\"ele_bg_hover\":\"\",\"ele_bg_type\":\"gradient\",\"ele_bg_gradient\":\"150,#44d3f6,23,#72e584,45,#2ca4eb,100\",\"pagelayer-id\":\"jm36390\"} -->{\"title\":\"Contactos\",\"form_id\":\"82\",\"show_title\":\"1\",\"show_desc\":\"1\"}<!-- /wp:pagelayer/pl_wp_wpforms-widget -->\n<!-- wp:pagelayer/pl_text {\"text\":\"\\u003cp\\u003e\\u003cbr\\u003e\\u003c/p\\u003e\",\"pagelayer-id\":\"zg94860\"} --><p><br></p><!-- /wp:pagelayer/pl_text -->\n<!-- /wp:pagelayer/pl_col -->\n<!-- /wp:pagelayer/pl_row -->\n', 'Inicio', '', 'inherit', 'closed', 'closed', '', '44-revision-v1', '', '', '2023-07-05 18:44:16', '2023-07-05 23:44:16', '', 44, 'http://localhost/wordpress/?p=97', 0, 'revision', '', 0);
